let assert = require('assert');

describe('arrow functions', function() {
    it('should support single params', function() {
        let squared = x => x * x;

        assert.equal(squared(4), 16);
    });

    it('should support multiple params', function() {
        let subtract = (a, b) => a - b;

        assert.equal(subtract(10, 7), 3);
    });

    it('should support no params', function() {
        let pi = () => 3.1415;

        assert.equal(pi(), 3.1415);
    });
});
