let assert = require('assert');

describe('default function parameters', function() {
    it('should support a single default param', function() {
        function sayMsg(msg='This is a default message.') {
            return msg;
        }

        assert.equal(sayMsg(), 'This is a default message.');
        assert.equal(sayMsg('This is NOT a default message.'), 'This is NOT a default message.');
    });

    it('should support multiple default params', function() {
        function sayMsg(msg='This is a default message', punc='.') {
            return msg + punc;
        }

        assert.equal(sayMsg(), 'This is a default message.');
        assert.equal(sayMsg('This is NOT a default message'), 'This is NOT a default message.');
        assert.equal(sayMsg('This is NOT a default message', '!'), 'This is NOT a default message!');
    });

    it('should support mixed params', function() {
        function sayMsg(msg, punc='.') {
            return msg + punc;
        }

        assert.equal(sayMsg(), 'undefined.');
        assert.equal(sayMsg('This is NOT a default message'), 'This is NOT a default message.');
        assert.equal(sayMsg('This is NOT a default message', '!'), 'This is NOT a default message!');
    });
});
