let assert = require('assert');

describe('rest parameters', function() {
    it('should support rest parameters', function() {
        function format(str, ...args) {
            return str.replace(/\{\s*(\d+)\s*\}/g, (m, n) => args[n]);
        }

        let msg = format('Hi, this is a {0} {1}, how cool.', 'format', 'function');

        assert.equal(msg, 'Hi, this is a format function, how cool.');
    });
});
